import "@/styles/globals.css";
import type { AppProps } from "next/app";
import { ConfigProvider } from "antd";
import { customTheme } from "@/config";
import { ArweaveWalletKit } from "arweave-wallet-kit";
import Head from "next/head";
import { ConnectButton } from "arweave-wallet-kit";
import { Image, Layout, Row, theme } from "antd";
const { Header } = Layout;
import React from "react";

const { getDesignToken } = theme;

const globalToken = getDesignToken(customTheme);

const headerStyle: React.CSSProperties = {
  height: 54,
  lineHeight: "54px",
  backgroundColor: globalToken.colorBgBase,
  color: "black",
  boxShadow: "0 2px 3px -2px rgba(0,0,0,0.3)",
};

const noOverlayWorkaroundScript = `
    window.addEventListener('error', event => {
      event.stopImmediatePropagation()
    })

    window.addEventListener('unhandledrejection', event => {
      event.stopImmediatePropagation()
    })
`;

export default function App({ Component, pageProps }: AppProps) {
  return (
    <ConfigProvider theme={customTheme}>
      <ArweaveWalletKit
        config={{
          permissions: ["ACCESS_ADDRESS", "SIGN_TRANSACTION", "DISPATCH"],
          ensurePermissions: true,
          appInfo: {
            name: "LicenseFolio",
            logo: "data:image/png;base64,",
          },
        }}
        theme={{
          accent: { r: 166, g: 42, b: 34 },
        }}
      >
        <Head>
          <title>LicenseFolio</title>
          <meta name="description" content="Generated by create next app" />
          <meta name="viewport" content="width=device-width, initial-scale=1" />
          <link
            rel="icon"
            href="data:image/png;base64,"
          />
          {process.env.NODE_ENV !== "production" && (
            <script
              dangerouslySetInnerHTML={{ __html: noOverlayWorkaroundScript }}
            />
          )}
        </Head>
        <main>
          <Layout>
            <Header style={headerStyle}>
              <Row justify="space-between" align="middle">
                <Image
                  width="38px"
                  src="data:image/png;base64,"
                  alt="logo"
                  style={{ borderRadius: "50%" }}
                  preview={false}
                />

                <ConnectButton
                  accent={globalToken.colorPrimary}
                  style={{
                    height: "34px",
                  }}
                />
              </Row>
            </Header>

            <Component {...pageProps} />
          </Layout>
        </main>
      </ArweaveWalletKit>
    </ConfigProvider>
  );
}
